mapper.update:
UPDATE sys_sale
SET
del_flag = 1,

modifier = #{modifier}
WHERE id = #{id}

mapper.select:
select s.*,c.commodity_name ,c.commodity_num, c.photo photo,c.class_name className,p.way_name,a.artist_name from sys_sale s left join sys_commodity c on s.commodity_id = c.id left join sys_plat p on s.sale_way = p.id left join sys_artist a on s.artist_id = a.id where s.del_flag = 0
AND c.commodity_name like CONCAT('%', #{commodityName}, '%')
AND c.commodity_num like CONCAT('%', #{commodityNum}, '%')
AND c.class_name like CONCAT('%', #{className}, '%')
AND p.way_name = #{wayName}
AND s.artist_id = #{artistId}
AND s.sale_way = #{saleWay}
AND s.sale_date >= #{startDate}
AND s.sale_date <=#{endDate} order by s.create_date desc
